import styled, {css} from "styled-components";
import checkboxSVG from '@icon/tab-checkbox.svg';
import delSVG from '@icon/tab-delete.svg';

export const Container = styled.div `
    padding-top: 28px;
`
export const InputWrapper = styled.form `
    display: flex;
    height: 44px;
`
export const Label = styled.label `
    width: 100%;
    height: 100%;
`

export const TodoField = styled.input `
    display: inline-block;
    width: calc( 100% - 79px);
    height: 100%;
    padding: 12px 16px;
    font-family: 'Pretendard';
    font-size: ${props =>props.theme.Body1.FontSize};
    font-weight: ${props =>props.theme.Body1.FontWeight};
    border: 1px solid ${props=> props.theme.colors.Gray200};
    border-radius: ${props=> props.theme.Radius.BRadius};
    &::placeholder {
        color: ${props => props.theme.colors.Gray400};
        font-size: 16px;
    }
`


export const AddButton = styled.button `
    display: inline-block;
    margin-left: 20px;
    width: 59px;
    height: 100%;
    color: ${props => props.theme.colors.white};
    font-size:  ${props => props.theme.Button1.FontSize};
    font-weight: ${props => props.theme.Button1.FontWeight};
    line-height: ${props => props.theme.Button1.LineHight};
    background-color: ${props=>props.theme.colors.Primary};
    border: none;
    border-radius: ${props=> props.theme.Radius.BRadius};
    cursor: pointer;
`
export const DelButton = styled.button`
    display: none;
    position: absolute;
    right: 0;
    top: 0;
    background-image: url(${delSVG});
    background-repeat: no-repeat;
    background-size: 24px, 24px;
    background-position: center center;
    border: none;
    outline: none;
    background-color: transparent;
    text-indent: -9999px;
    overflow: hidden;
    width:30px;
    height: 30px;
    cursor: pointer;
`

export const ContentsWrapper = styled.ul ` 
    margin-top: 34px;

    `
export const TodoItem = styled.li` 
    position: relative;
    margin-bottom: 21px;

    label {
        position: relative;
        display: block;

        input[type="checkbox"] {
            position: absolute;
            top: 0;
            left: 0;
            transform: translate(0, 0);
            margin: 0;
            width: 30px;
            height: 30px;
            opacity: 0;

            &:checked {
                &+span {
                    color: ${props => props.theme.colors.Gray400};

                    &::before {
                        background-image: url(${checkboxSVG});
                        background-position: -6px -6px;
                        background-repeat: no-repeat;
                        border-color: ${props=>props.theme.colors.Primary};
                    }
                }
            }

            & + span {
                position: relative;
                display: inline-block;
                padding-left: 44px;
                line-height: 30px;
                color: ${props=>props.theme.colors.Gray800};
                font-size: ${props=>props.theme.Body1.FontSize};
                font-weight: ${props=>props.theme.Body1.FontWeight};
                cursor: pointer;
                width: calc( 100% - 54px);
                overflow:hidden;
                text-overflow:ellipsis;
                white-space:nowrap;

                &::before {
                    content: '';
                    position: absolute;
                    top: 0;
                    left: 0;
                    display: inline-block;
                    width: 30px;
                    height: 30px;
                    border: 3px solid ${props => props.theme.colors.Gray200};
                    border-radius: 50%;
                    overflow: hidden;
                    box-sizing: border-box;
                }
            }
        }
    }


    &:last-of-type{
        margin-bottom: 0;
    }

    &:hover {
        ${DelButton} {
            display: block;
        }
    }

`
